$(function(){if($(".read-box").size()!=0){var d=[{no:0,size:16,lineHeight:26},{no:1,size:18,lineHeight:28},{no:2,size:20,lineHeight:30},{no:3,size:22,lineHeight:32},{no:4,size:24,lineHeight:34}];var a=1;var b=getCookie("sizeNo");if(b!=""){a=b;$(".read-content").css({"font-size":d[a].size+"px","line-height":d[a].lineHeight+"px"})}var c=getCookie("dayORnight");if(c=="night"){$(".m-night").addClass("m-day");$("body").addClass("read-black")}$(".m-fd").click(function(){a++;(a>4)?a=4:a=a;var f=d[a].lineHeight;var e=d[a].size;$(".read-content").css({"font-size":e+"px","line-height":f+"px"});addCookie("sizeNo",a,30*24);if(a==d[d.length-1].no){$(this).addClass("gray")}else{$(".m-fd,.m-sx").removeClass("gray")}});$(".m-sx").click(function(){a--;(a<0)?a=0:a=a;var f=d[a].lineHeight;var e=d[a].size;$(".read-content").css({"font-size":e+"px","line-height":f+"px"});addCookie("sizeNo",a,30*24);if(a==d[0].no){$(this).addClass("gray")}else{$(".m-fd,.m-sx").removeClass("gray")}});$(".m-night").click(function(){if($(".read-black").size()==0){$("body").addClass("read-black");$(this).addClass("m-day");addCookie("dayORnight","night",30*24)}else{$("body").removeClass("read-black");$(this).removeClass("m-day");addCookie("dayORnight","day",30*24)}})}});function addCookie(c,e,b){var d=c+"="+escape(e);if(b>0){var a=new Date();a.setTime(a.getTime()+b*3600*1000);d=d+";expires="+a.toUTCString()+";path=/"}document.cookie=d}function getCookie(c){var b=document.cookie;var e=b.split(";");var d=e.length;for(var a=0;a<d;a++){var f=e[a].split("=");if(c==f[0].trim()){return unescape(f[1])}}return""};