$(function(){if($(".read-box").size()!=0){var g=[{no:0,size:16,lineHeight:26},{no:1,size:18,lineHeight:28},{no:2,size:20,lineHeight:30},{no:3,size:22,lineHeight:32},{no:4,size:24,lineHeight:34}];var f=1;var e=getCookie("sizeNo");if(e!=""){f=e;$(".read-content").css({"font-size":g[f].size+"px","line-height":g[f].lineHeight+"px"})}var h=getCookie("dayORnight");if(h=="night"){$(".m-night").addClass("m-day");$("body").addClass("read-black")}$(".m-fd").click(function(){f++;(f>4)?f=4:f=f;var a=g[f].lineHeight;var b=g[f].size;$(".read-content").css({"font-size":b+"px","line-height":a+"px"});addCookie("sizeNo",f,30*24);if(f==g[g.length-1].no){$(this).addClass("gray")}else{$(".m-fd,.m-sx").removeClass("gray")}});$(".m-sx").click(function(){f--;(f<0)?f=0:f=f;var a=g[f].lineHeight;var b=g[f].size;$(".read-content").css({"font-size":b+"px","line-height":a+"px"});addCookie("sizeNo",f,30*24);if(f==g[0].no){$(this).addClass("gray")}else{$(".m-fd,.m-sx").removeClass("gray")}});$(".m-night").click(function(){if($(".read-black").size()==0){$("body").addClass("read-black");$(this).addClass("m-day");addCookie("dayORnight","night",30*24)}else{$("body").removeClass("read-black");$(this).removeClass("m-day");addCookie("dayORnight","day",30*24)}})}});function addCookie(j,h,f){var i=j+"="+escape(h);if(f>0){var g=new Date();g.setTime(g.getTime()+f*3600*1000);i=i+";expires="+g.toUTCString()+";path=/"}document.cookie=i}function getCookie(l){var g=document.cookie;var j=g.split(";");var k=j.length;for(var h=0;h<k;h++){var i=j[h].split("=");if(l==i[0].trim()){return unescape(i[1])}}return""};